import React, { useState } from 'react'
import './Form.css'

const Form = () => {
    const [name, setName] = useState({
        fName : "",
        lName : "",
    })
    const[fullName, setFullName] = useState({
        fName: "",
        lName:"",
    });
    const inputEvent = (event) => {
        const {value, name} = event.target;
        setName((preVal)=>{
            return{
                ...preVal,
                [name] : value,
            }
        })
    }
    
    const onSubmit = (event) => {
        event.preventDefault();
        setFullName(name);
    }

    return(
        <>
            <div className='main_div'>
                <form className='div_form' onSubmit={onSubmit}>
                    <h1>Hello {fullName.fName} {fullName.lName}</h1>
                    <input type="text" placeholder='Enter Your First Name' name='fName' onChange={inputEvent} value={name.fName} /><br/>
                    <input type="text" placeholder='Enter Your Last Name' name='lName' onChange={inputEvent}  value={name.lName} /><br/>
                    <button type='submit'>Submit Me</button>
                </form>
            </div>
        </>
    );
}

export default Form;
