import React, { useState } from 'react';
import "./Form.css"

const Form = () => {
    const [name, setName] = useState({
        fName : "",
        lName : "",
        email:"",
        phone:""
    })
    const [fullName, setFullName] = useState({
        fName : "",
        lName : "",
        email:"",
        phone:"",
    })
    const inputEvent = (event) => {
        const {value,name} = event.target;
        setName((preVal)=>{
            return{
                ...preVal,
                [name] : value
            }
        })
    }
    const onSubmit = (event) => {
        event.preventDefault();
        setFullName(name);
        setName({
            fName:"",
            lName:"",
            email:"",
            phone:""
        });
    }
    return(
        <>
            <div className='main_div'>
                <form className='div_form' onSubmit={onSubmit}>
                    <h1>Hello {fullName.fName} {fullName.lName}</h1>
                    <h3>{fullName.email}</h3>
                    <p>{fullName.phone}</p>
                    <input type="text" name='fName' placeholder='Enter Your First Name' onChange={inputEvent} value={name.fName} />
                    <input type="text" name='lName' placeholder='Enter Your Last Name' onChange={inputEvent} value={name.lName} />
                    <input type="email" name='email' placeholder='Enter Your Email' onChange={inputEvent} value={name.email} />
                    <input type="number" name='phone' placeholder='Enter Your Phone' onChange={inputEvent} value={name.phone} />
                    <button type='submit'>Submit Me</button>
                </form>
            </div>
        </>
    );
}

export default Form;
