import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'

let greeting = ""
let backColor = ""
const time = new Date().getHours();
if(1 <= time <= 11 ){
  greeting = "Good Morning"
  backColor = "green"
}else if( 11 < time <= 19 ){
  greeting = "Good After Noon"
  backColor = "orange"
}
else{
  greeting = "Good Night"
  backColor = "blue"
}


ReactDOM.render(
  <>
    <div className='main_div'>
      <div className='center_div'>
      <h1>Hello Sir <span style={{backgroundColor:"white", color:{backColor}}}>{greeting}</span></h1>
      </div>
    </div>
  </>,
  document.getElementById('root')
);
