import React, { useState } from 'react'
import './Form.css'

const Form = ()=> {

    const [name, setName] = useState({
        fname : "",
        lname : "",
        email : "",
        phone : "",
    });

    const inputEvent = (event) => {
        const {name, value} = event.target;
        setName((preVal)=>{
            return{
                ...preVal,
                [name] : value,
            }
        })
    }

    const onSubmit = () => {

    }

    return(
        <>
           <div className='main_div'>
            <form className='form_sub' onSubmit={onSubmit}>
                <div className='div_form'>
                    <h1>Hello {name.fname} {name.lname}</h1>
                    <h2>{name.email}</h2>
                    <h3>{name.phone}</h3>
                    <input className='input-box' type="text" name='fname' placeholder='Enter Your First Name' onChange={inputEvent} value={name.fname}  /><br/>
                    <input className='input-box' type="text" name='lname' placeholder='Enter Your Last Name' onChange={inputEvent} value={name.lname} /><br/>
                    <input className='input-box' type="email" name='email' placeholder='Enter Your Email' onChange={inputEvent} value={name.email} /><br/>
                    <input className='input-box' type="number" name='phone' placeholder='Enter Your Phone' onChange={inputEvent} value={name.phone} /><br/>
                    <button type='submit'>Click Me</button>
                </div>
            </form>
           </div>
        </>
    );
}

export default Form;
