import React from 'react'
import './App.css'

let greeting = "";
const hours = new Date().setHours(11);
if(hours > 0 && hours <= 11){
    greeting="Good Morning";
}
else if(hours > 11 && hours<=19){
    greeting="Good Afternoon";
}
else{
    greeting="Good Night";
}

const App = () => {
    return(
        <>
            <div className='main_div'>
                <h1>Hello sir!! <span>{greeting}</span></h1>
            </div>
        </>
    );
}

export default App;
